mapper.select:
SELECT m.MOVIE_CODE as movieCode, m.MOVIE_NAME as movieName, m.POSTER as poster, v.PRICE as dvdPrice, v.DISCOUNT as dvdDiscount FROM MOVIE m LEFT JOIN VOD v ON m.MOVIE_CODE = v.MOVIE_CODE
AND m.MOVIE_NAME LIKE CONCAT('%', #{movieName}, '%')
GROUP BY m.MOVIE_CODE ORDER BY m.MOVIE_CODE DESC LIMIT #{size} OFFSET #{offset}

SELECT EXISTS (
  SELECT 1 FROM GENRE WHERE GENRE_CODE = #{genreCode}
)

SELECT EXISTS (
  SELECT 1 FROM MOVIE WHERE MOVIE_NAME = #{movieName}
)

mapper.update:
UPDATE VOD SET
  PRICE = #{price},
  START_DATE = #{startDate},
  END_DATE = #{endDate},
  DISCOUNT = #{discount}
WHERE MOVIE_CODE = #{movieCode}
