mapper.select:
SELECT m.USER_ID, m.PASSWD, m.USER_NAME, m.EMAIL, m.TEL, m.AGE, m.SIGNUP_DATE, m.TOKEN, m.TOKEN_VALIDITY, m.VIEW_ADULT, m.SAVE_HISTORY, m.TERMS, m.USER_TPCD, c1.COMMON_NAME as USER_TPCD_NAME, m.LANG_TPCD, c2.COMMON_NAME as LANG_TPCD_NAME, m.DATE_TPCD, c3.COMMON_NAME as DATE_TPCD_NAME FROM MEMBERS m LEFT JOIN COMMON_CODE c1 ON m.USER_TPCD = c1.COMMON_VALUE AND c1.COMMON_CODE = 'USER_TPCD' LEFT JOIN COMMON_CODE c2 ON m.LANG_TPCD = c2.COMMON_VALUE AND c2.COMMON_CODE = 'LANG_TPCD' LEFT JOIN COMMON_CODE c3 ON m.DATE_TPCD = c3.COMMON_VALUE AND c3.COMMON_CODE = 'DATE_TPCD';
WHERE m.USER_ID = #{userId}
AND m.PASSWD = #{passwd}
